                fieldWysiwyg: {
                  processed:
                    '<h2 dir="ltr"><strong>Apply for PIRATE</strong></h2>\n\n<p dir="ltr">If you or a family member is interested in participating in the PIRATE program, please complete an application and return it to Mary Sullivan. You may fax or mail your completed application.</p>\n\n<p class="va-address-block" dir="ltr"><strong>Fax: </strong><a href="tel: +14123606426">412-360-6426</a><br /><strong>Mail:</strong><br />\nVA Pittsburgh Healthcare System<br />\nPIRATE, ATTN Mary Sullivan<br />\nUniversity Drive, 112 SP-U<br />\nPittsburgh, PA 15240</p>',
                },
                entityId: '7004',
