                  fieldWysiwyg: {
                    processed:
                      '<h3>INTERNSHIP INFO</h3>\n\n<h4>APPIC Match Numbers</h4>\n\n<p>General Track: 154311</p>\n\n<p>Neuropsychology Track: 154312</p>\n\n<p>Geropsychology Track: 154313</p>\n\n<h4>Applications Due</h4>\n\n<p>Internship: November 8, 2020</p>\n\n<p>Postdoctoral Fellowship: January 3, 2021</p>\n\n<h4>Eligibility Requirements</h4>\n\n<p>Review the <a href="http://www.psychologytraining.va.gov/eligibility.asp">eligibility requirements</a> for all VA psychology training programs.</p>\n\n<h4 id="contact-info">Contact Info</h4>\n\n<p><strong>Bernadette Pasquale, Ph.D.</strong><br />\nDirector of Clinical Training<br />\nUniversity Drive campus<br />\n412-360-1290<br /><a href="mailto:bernadette.pasquale@va.gov" title="email <b>Bernadette Pasquale, Ph.D.</b>">bernadette.pasquale@va.gov</a></p>',
                  },
